CREATE TABLE CARTITEMS (cartline_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, image VARCHAR(255), line_number INTEGER NOT NULL, price_each DOUBLE, quantity INTEGER, sku_number VARCHAR(255) NOT NULL, cart_id BIGINT NOT NULL, PRIMARY KEY (cartline_id));
CREATE TABLE cart (cart_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, USER_user_id BIGINT, PRIMARY KEY (cart_id));
CREATE TABLE order_details (line_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, line_number INTEGER NOT NULL, price_each DOUBLE NOT NULL, quantity_ordered INTEGER NOT NULL, sku_number VARCHAR(255) NOT NULL, order_id BIGINT NOT NULL, PRIMARY KEY (line_id));
CREATE TABLE users (user_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, ADDRESS VARCHAR(50), CITY VARCHAR(50), COUNTRY VARCHAR(3), EMAIL VARCHAR(320) NOT NULL UNIQUE, first_name VARCHAR(50) NOT NULL, last_name VARCHAR(50) NOT NULL, PASSWORD VARCHAR(60) NOT NULL, STATE VARCHAR(50), ZIPCODE VARCHAR(20), PRIMARY KEY (user_id));
CREATE TABLE products (product_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, DESCRIPTION LONGTEXT, product_image VARCHAR(255) NOT NULL, PRICE DOUBLE NOT NULL, product_name VARCHAR(50) NOT NULL UNIQUE, quantity_in_stock INTEGER NOT NULL, category_id BIGINT NOT NULL, PRIMARY KEY (product_id));
CREATE TABLE shipments (shipment_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, date_delivered DATE, date_shipped DATE NOT NULL, order_id BIGINT NOT NULL, PRIMARY KEY (shipment_id));
CREATE TABLE orders (order_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, date_ordered DATE NOT NULL, order_status VARCHAR(15), user_id BIGINT, PRIMARY KEY (order_id));
CREATE TABLE category (category_id BIGINT AUTO_INCREMENT NOT NULL UNIQUE, category_name VARCHAR(20) NOT NULL UNIQUE, category_image VARCHAR(255) NOT NULL, PRIMARY KEY (category_id));
ALTER TABLE CARTITEMS ADD CONSTRAINT FK_CARTITEMS_cart_id FOREIGN KEY (cart_id) REFERENCES cart (cart_id);
ALTER TABLE cart ADD CONSTRAINT FK_cart_USER_user_id FOREIGN KEY (USER_user_id) REFERENCES users (user_id);
ALTER TABLE order_details ADD CONSTRAINT FK_order_details_order_id FOREIGN KEY (order_id) REFERENCES orders (order_id);
ALTER TABLE products ADD CONSTRAINT FK_products_category_id FOREIGN KEY (category_id) REFERENCES category (category_id);
ALTER TABLE shipments ADD CONSTRAINT FK_shipments_order_id FOREIGN KEY (order_id) REFERENCES orders (order_id);
ALTER TABLE orders ADD CONSTRAINT FK_orders_user_id FOREIGN KEY (user_id) REFERENCES users (user_id);
